// Live2D核心代码
function initLive2d() {
    const live2d = document.createElement('div');
    live2d.id = 'waifu';
    live2d.innerHTML = `
        <div class="waifu-tips"></div>
        <canvas id="live2d" width="280" height="250"></canvas>
        <div class="waifu-tool">
            <span class="fa fa-home"></span>
            <span class="fa fa-comments"></span>
            <span class="fa fa-camera"></span>
            <span class="fa fa-times"></span>
        </div>`;
    document.body.appendChild(live2d);

    // 加载Live2D模型
    loadlive2d('live2d', 'https://cdn.jsdelivr.net/gh/stevenjoezhang/live2d-widget-models@latest/packages/live2d-widget-model-koharu/assets/koharu.model.json');
}

// 添加样式
const style = document.createElement('style');
style.innerHTML = `
#waifu {
    position: fixed;
    right: 0;
    bottom: 0;
    width: 280px;
    height: 250px;
    z-index: 1000;
    font-size: 0;
    transition: all .3s ease-in-out;
    transform-origin: right bottom;
}
.waifu-tips {
    opacity: 0;
    width: 250px;
    height: 70px;
    margin: -20px 20px;
    padding: 5px 10px;
    border-radius: 12px;
    background-color: rgba(255, 105, 180, 0.9);
    box-shadow: 0 3px 15px rgba(0, 0, 0, 0.2);
    font-size: 12px;
    text-overflow: ellipsis;
    overflow: hidden;
    position: absolute;
    animation-delay: 5s;
    animation-duration: 50s;
    animation-iteration-count: infinite;
    animation-name: shake;
    animation-timing-function: ease-in-out;
}
#live2d {
    position: relative;
    cursor: grab;
    cursor: -webkit-grab;
    cursor: -o-grab;
    cursor: -ms-grab;
}
#live2d:active {
    cursor: grabbing;
    cursor: -webkit-grabbing;
    cursor: -o-grabbing;
    cursor: -ms-grabbing;
}
.waifu-tool {
    display: none;
    position: absolute;
    right: 10px;
    bottom: 20px;
}
.waifu-tool span {
    display: block;
    cursor: pointer;
    color: #FF69B4;
    line-height: 30px;
    text-align: center;
    font-size: 14px;
    transition: all .3s;
}
.waifu-tool span:hover {
    color: #7B68EE;
}
@keyframes shake {
    2% { transform: translate(0.5px, -1.5px) rotate(-0.5deg); }
    4% { transform: translate(0.5px, 1.5px) rotate(1.5deg); }
    6% { transform: translate(1.5px, 1.5px) rotate(1.5deg); }
    8% { transform: translate(2.5px, 1.5px) rotate(0.5deg); }
    10% { transform: translate(0.5px, 2.5px) rotate(0.5deg); }
    /* 添加更多关键帧... */
    100% { transform: translate(0, 0) rotate(0); }
}
@media (max-width: 768px) {
    #waifu {
        display: none;
    }
}`;
document.head.appendChild(style);

// 在页面加载完成后初始化看板娘
window.addEventListener('load', initLive2d);
